import { Icon } from "@iconify/react";
import { useRef, useState } from "react";
import editFill from "@iconify/icons-eva/edit-fill";
import { Link as RouterLink } from "react-router-dom";
import moreVerticalFill from "@iconify/icons-eva/more-vertical-fill";
import { updateStatus } from "src/service/OrderService";

// material
import {
	Menu,
	MenuItem,
	IconButton,
	ListItemIcon,
	ListItemText,
} from "@mui/material";

// ----------------------------------------------------------------------
const cancelOnClick = () => {
	const data = "Canceled";
	updateStatus(id, data).then((res) => {
		window.history.back();
	});
	setIsOpen(false);
};
const deliveredOnClick = () => {};
const shippedOnClick = () => {};

export default function OrderMoreMenu({
	cancelHandler,
	id,
}) {
	const ref = useRef(null);
	const [isOpen, setIsOpen] = useState(false);

	return (
		<>
			<IconButton ref={ref} onClick={() => setIsOpen(true)}>
				<Icon
					icon={moreVerticalFill}
					width={20}
					height={20}
				/>
			</IconButton>

			<Menu
				open={isOpen}
				anchorEl={ref.current}
				onClose={() => setIsOpen(false)}
				PaperProps={{
					sx: { width: 200, maxWidth: "100%" },
				}}
				anchorOrigin={{
					vertical: "top",
					horizontal: "right",
				}}
				transformOrigin={{
					vertical: "top",
					horizontal: "right",
				}}>
				<MenuItem
					onClick={shippedOnClick}
					sx={{ color: "text.secondary" }}>
					<ListItemText
						primary="Shipped"
						primaryTypographyProps={{ variant: "body1" }}
					/>
				</MenuItem>
				<MenuItem
					onClick={deliveredOnClick}
					sx={{ color: "text.secondary" }}>
					<ListItemText
						primary="Delivered"
						primaryTypographyProps={{ variant: "body1" }}
					/>
				</MenuItem>
				<MenuItem
					onClick={cancelOnClick}
					sx={{ color: "text.secondary" }}>
					<ListItemText
						primary="Cancelled"
						primaryTypographyProps={{ variant: "body1" }}
					/>
				</MenuItem>
			</Menu>
		</>
	);
}
